import 'package:flutter/material.dart';import 'package:flutter/services.dart';class SingUpNumber extends StatefulWidget {  @override  _SingUpNumberState createState() => _SingUpNumberState();}class _SingUpNumberState extends State<SingUpNumber> {  //bool _isFacebookOn = false;  Widget buildName() {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text(          "Mobil nömrə:",          style: TextStyle(            fontFamily: "Ubuntu",            color: Colors.white,            fontSize: 14,          ),        ),        SizedBox(          height: 20,        ),        Container(          alignment: Alignment.centerLeft,          decoration: BoxDecoration(              gradient: LinearGradient(                colors: [                  const Color(0xFF676D9E),                  const Color(0xFF31365D),                ],                begin: const FractionalOffset(0.0, 0.0),                end: const FractionalOffset(1.0, 0.0),                stops: [0.0, 1.0],                tileMode: TileMode.clamp,              ),              borderRadius: BorderRadius.circular(40),              boxShadow: [                BoxShadow(                    color: Colors.black54, blurRadius: 8, offset: Offset(0, 2))              ]),          height: 50,          child: TextField(            keyboardType: TextInputType.number,            style: TextStyle(color: Colors.white54),            decoration: InputDecoration(              border: InputBorder.none,              contentPadding:                  EdgeInsets.symmetric(horizontal: 30, vertical: 20),              prefixIcon: Icon(                Icons.check,                color: Colors.green,              ),            ),          ),        ),      ],    );  }  Widget buildOrDivider() {    Size size = MediaQuery.of(context).size;    return Container(      margin: EdgeInsets.symmetric(vertical: 20),      width: size.width * 0.7,      child: Row(        children: [          buildDivider(),          Padding(            padding: const EdgeInsets.symmetric(horizontal: 6),            child: Text(              "və ya",              style: TextStyle(                fontFamily: "Ubuntu",                color: Colors.white,                fontSize: 12,              ),            ),          ),          buildDivider(),        ],      ),    );  }  Expanded buildDivider() {    return Expanded(        child: Divider(      color: Colors.white,      height: 2,    ));  }  Widget buildIcons() {    return Row(      crossAxisAlignment: CrossAxisAlignment.center,      mainAxisAlignment: MainAxisAlignment.center,      children: [        SocialIcon(          iconSrc: "images/facebook.png",          press: () {},        ),        SocialIcon(          iconSrc: "images/twitter.png",          press: () {},        ),        SocialIcon(          iconSrc: "images/googleplus.png",          press: () {},        ),      ],    );  }  Widget buildSignUpBtn() {    return Container(      padding: EdgeInsets.symmetric(vertical: 15),      child: TextButton(        onPressed: () => ("Login Pressed"),        child: Container(          decoration: BoxDecoration(            color: Color(0xff39c27c),            borderRadius: BorderRadius.circular(30.0),          ),          child: Text(            "Növbəti",            textAlign: TextAlign.center,            style: TextStyle(                fontSize: 20, color: Colors.black, fontFamily: "Ubuntu"),          ),          padding: EdgeInsets.symmetric(            vertical: 13,          ),          alignment: Alignment.center,        ),      ),    );  }  Widget buildClickSignUp() {    return Column(      crossAxisAlignment: CrossAxisAlignment.center,      children: [        Padding(          padding: const EdgeInsets.symmetric(horizontal: 60),          child: Text(            "Öncədən qeydiyyatdan keçmisiniz? O zaman buraya tıklayın və daxil "            "olun",            style: TextStyle(              fontFamily: "Ubuntu",              color: Colors.white,              fontSize: 10,            ),          ),        ),      ],    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: AnnotatedRegion<SystemUiOverlayStyle>(        value: SystemUiOverlayStyle.light,        child: GestureDetector(          child: Stack(            children: [              Container(                width: double.infinity,                height: double.infinity,                decoration: BoxDecoration(                    gradient: LinearGradient(colors: [                  Color(0xff202343),                  Color(0xff202343),                ])),                child: SingleChildScrollView(                  padding: EdgeInsets.symmetric(                    horizontal: 30,                    vertical: 120,                  ),                  child: Column(                    mainAxisAlignment: MainAxisAlignment.center,                    children: [                      Text(                        "Qeydiyyat",                        style: TextStyle(                          fontFamily: "Ubuntu", color: Colors.white,                          fontSize: 30, //fontWeight: FontWeight.bold,                        ),                      ),                      SizedBox(                        height: 50,                      ),                      buildName(),                      SizedBox(                        height: 50,                      ),                      buildSignUpBtn(),                      SizedBox(                        height: 50,                      ),                      buildOrDivider(),                      SizedBox(                        height: 20,                      ),                      buildIcons(),                      SizedBox(                        height: 120,                      ),                      buildClickSignUp(),                    ],                  ),                ), // child: buildEmail(),              )            ],          ),        ),      ),    );  }}class SocialIcon extends StatelessWidget {  final String iconSrc;  final Function press;  const SocialIcon({Key key, this.iconSrc, this.press}) : super(key: key);  @override  Widget build(BuildContext context) {    return GestureDetector(        onTap: press,        child: Container(          margin: EdgeInsets.symmetric(horizontal: 20),          child: Image.asset(iconSrc),        ));  }}