import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';class login extends StatefulWidget {  @override  _LoginScreenState createState() => _LoginScreenState();}class _LoginScreenState extends State<login> {  bool isRememberMe = false;  Widget buildName() {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text(          "Ad və Soyad:",          style: TextStyle(            fontFamily: "Ubuntu",            color: Colors.white,            fontSize: 14,          ),        ),        SizedBox(          height: 20,        ),        Container(          alignment: Alignment.centerLeft,          decoration: BoxDecoration(              gradient: LinearGradient(                colors: [                  const Color(0xFF676D9E),                  const Color(0xFF31365D),                ],                begin: const FractionalOffset(0.0, 0.0),                end: const FractionalOffset(1.0, 0.0),                stops: [0.0, 1.0],                tileMode: TileMode.clamp,              ),              borderRadius: BorderRadius.circular(40),              boxShadow: [                BoxShadow(                    color: Colors.black54, blurRadius: 8, offset: Offset(0, 2))              ]),          height: 50,          child: TextField(            keyboardType: TextInputType.text,            style: TextStyle(color: Colors.white54),            decoration: InputDecoration(              border: InputBorder.none,              contentPadding:                  EdgeInsets.symmetric(horizontal: 30, vertical: 20),              prefixIcon: Icon(                Icons.check,                color: Colors.green,              ),            ),          ),        ),      ],    );  }  Widget buildPassword() {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text(          "Şifrə:",          style: TextStyle(            fontFamily: "Ubuntu",            color: Colors.white,            fontSize: 14,          ),        ),        SizedBox(          height: 20,        ),        Container(          alignment: Alignment.centerLeft,          decoration: BoxDecoration(              gradient: LinearGradient(                colors: [                  const Color(0xFF676D9E),                  const Color(0xFF31365D),                ],                begin: const FractionalOffset(0.0, 0.0),                end: const FractionalOffset(1.0, 0.0),                stops: [0.0, 1.0],                tileMode: TileMode.clamp,              ),              borderRadius: BorderRadius.circular(40),              boxShadow: [                BoxShadow(                    color: Colors.black54, blurRadius: 8, offset: Offset(0, 2))              ]),          height: 50,          child: TextField(            obscureText: true,            style: TextStyle(color: Colors.white54),            decoration: InputDecoration(              border: InputBorder.none,              contentPadding:                  EdgeInsets.symmetric(horizontal: 30, vertical: 20),              prefixIcon: Icon(                Icons.check,                color: Colors.green,              ),            ),          ),        ),      ],    );  }  Widget buildForgetPassword() {    return Container(      alignment: Alignment.centerRight,      child: TextButton(        onPressed: () => print("forgot password pressed"),        child: Text(          "Şifrəni unutmusunuz?",          style: TextStyle(            color: Colors.white,            fontSize: 10,          ),        ),      ),    );  }  Widget buildRememberMe() {    return Container(      height: 10,      child: Row(        children: [          Theme(              data: ThemeData(unselectedWidgetColor: Colors.green),              child: Checkbox(                value: isRememberMe,                checkColor: Colors.white,                activeColor: Colors.green,                onChanged: (value) {                  setState(() {                    isRememberMe = value;                  });                },              )),          Text(            "Məni xatırla",            style: TextStyle(              color: Colors.white,              fontSize: 10            ),          )        ],      ),    );  }  Widget buildSignUpBtn() {    return Container(      padding: EdgeInsets.symmetric(vertical: 15),      child: TextButton(        onPressed: () => ("Login Pressed"),        child: Container(          decoration: BoxDecoration(            color: Color(0xff39c27c),            borderRadius: BorderRadius.circular(30.0),          ),          child: Text(            "Növbəti",            textAlign: TextAlign.center,            style: TextStyle(                fontSize: 20, color: Colors.black, fontFamily: "Ubuntu"),          ),          padding: EdgeInsets.symmetric(            vertical: 13,          ),          alignment: Alignment.center,        ),      ),    );  }  Widget buildClickSignUp() {    return Column(      crossAxisAlignment: CrossAxisAlignment.center,      children: [        Padding(          padding: const EdgeInsets.symmetric(horizontal: 60),          child: Text(            "Öncədən qeydiyyatdan keçmisiniz? O zaman buraya tıklayın və daxil "            "olun",            style: TextStyle(              fontFamily: "Ubuntu",              color: Colors.white,              fontSize: 10,            ),          ),        ),      ],    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: AnnotatedRegion<SystemUiOverlayStyle>(        value: SystemUiOverlayStyle.light,        child: GestureDetector(          child: Stack(            children: [              Container(                width: double.infinity,                height: double.infinity,                decoration: BoxDecoration(                    gradient: LinearGradient(colors: [                  Color(0xff202343),                  Color(0xff202343),                ])),                child: SingleChildScrollView(                  padding: EdgeInsets.symmetric(                    horizontal: 30,                    vertical: 120,                  ),                  child: Column(                    mainAxisAlignment: MainAxisAlignment.center,                    children: [                      Text(                        "Qeydiyyat",                        style: TextStyle(                          fontFamily: "Ubuntu", color: Colors.white,                          fontSize: 30, //fontWeight: FontWeight.bold,                        ),                      ),                      SizedBox(                        height: 40,                      ),                      buildName(),                      SizedBox(                        height: 20,                      ),                      buildPassword(),                      SizedBox(                        height: 5,                      ),                      buildForgetPassword(),                      buildRememberMe(),                      buildSignUpBtn(),                      SizedBox(                        height: 180,                      ),                      buildClickSignUp()                    ],                  ),                ), // child: buildEmail(),              )            ],          ),        ),      ),    );  }}