import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:sms_autofill/sms_autofill.dart';class Otp extends StatefulWidget {  @override  _OtpState createState() => _OtpState();}class _OtpState extends State<Otp> {  Widget buildCode(){    return Padding(      padding: const EdgeInsets.all(50.0),      child: PinFieldAutoFill(        codeLength: 4,        decoration: UnderlineDecoration(          textStyle: TextStyle(              fontWeight: FontWeight.bold,              color: Colors.white),          colorBuilder: FixedColorBuilder(              Colors.grey),        ),        onCodeChanged: (val){          print(val);        },      ),    );  }  Widget buildName() {    return Text(      "Nömrəyə göndərilən kodu daxil edin",      style: TextStyle(        fontFamily: "Ubuntu",        color: Colors.white,        fontSize: 15,      ),    );  }  Widget buildBtn() {    return TextButton(      onPressed: () async{        final signcode = await SmsAutoFill().getAppSignature;        print(signcode);        Navigator.push(context, MaterialPageRoute(builder: (c) => Otp()));      },      child: Container(        decoration: BoxDecoration(          color: Color(0xff39c27c),          borderRadius: BorderRadius.circular(30.0),        ),        child: Text(          "Növbəti",          textAlign: TextAlign.center,          style: TextStyle(              fontSize: 20, color: Colors.black, fontFamily: "Ubuntu"),        ),        padding: EdgeInsets.symmetric(          vertical: 13,        ),        alignment: Alignment.center,      ),    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: Container(        width: double.infinity,        height: double.infinity,        decoration: BoxDecoration(            gradient: LinearGradient(colors: [          Color(0xff202343),          Color(0xff202343),        ])),        child: Padding(          padding: const EdgeInsets.symmetric(horizontal: 50),          child: Column(            mainAxisAlignment: MainAxisAlignment.center,            children: [              buildCode(),              SizedBox(                height: 30,              ),              buildName(),              SizedBox(                height: 80,              ),              buildBtn(),            ],          ),        ),      ), // child: buildEmail(),    );  }}