import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';class Successful extends StatefulWidget {  @override  _SuccesState createState() => _SuccesState();}class _SuccesState extends State<Successful> {  Widget buildIcon() {    return Icon(      Icons.check,      color: Colors.green,      size: 60,    );  }  Widget buildMessage() {    return Padding(      padding: const EdgeInsets.all(30),      child: Text(        "Siz uğurla qeydiyyatı bitirdiniz!",        style: TextStyle(          fontFamily: "Ubuntu",          color: Colors.green,          fontSize: 25,        ),      ),    );  }  Widget buildSignUpBtn() {    return Container(      padding: EdgeInsets.symmetric(vertical: 15),      child: TextButton(        onPressed: () => ("Login Pressed"),        child: Container(          decoration: BoxDecoration(            color: Color(0xff39c27c),            borderRadius: BorderRadius.circular(30.0),          ),          child: Text(            "Növbəti",            textAlign: TextAlign.center,            style: TextStyle(                fontSize: 20, color: Colors.black, fontFamily: "Ubuntu"),          ),          padding: EdgeInsets.symmetric(            vertical: 13,          ),          alignment: Alignment.center,        ),      ),    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: AnnotatedRegion<SystemUiOverlayStyle>(        value: SystemUiOverlayStyle.light,        child: GestureDetector(          child: Stack(            children: [              Container(                width: double.infinity,                height: double.infinity,                decoration: BoxDecoration(                    gradient: LinearGradient(colors: [                  Color(0xff202343),                  Color(0xff202343),                ])),                child: SingleChildScrollView(                  padding: EdgeInsets.symmetric(                    horizontal: 30,                    vertical: 200,                  ),                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.center,                    mainAxisAlignment: MainAxisAlignment.center,                    children: [                      buildIcon(),                      buildMessage(),                      SizedBox(                        height: 60,                      ),                      buildSignUpBtn(),                    ],                  ),                ), // child: buildEmail(),              )            ],          ),        ),      ),    );  }}