import 'package:animated_bottom_navigation_bar/animated_bottom_navigation_bar.dart';import 'package:flutter/material.dart';import 'bottom_bar_controller.dart';import 'package:get/get.dart';var iconList  = [  Icons.home,  Icons.shopping_cart_outlined,  Icons.shopping_basket,  Icons.person_outline,];class BottomBar extends StatelessWidget {  final controller = Get.put(BottomBarController());  @override  Widget build(BuildContext context) {    return AnimatedBottomNavigationBar(      backgroundColor: Color(0xff202343),      inactiveColor: Colors.white,      activeColor: Colors.green,      notchSmoothness: NotchSmoothness.verySmoothEdge,      gapLocation: GapLocation.center,      icons: iconList,      activeIndex: controller.getIndex(),      onTap: (index)=>controller.setIndex(index),    );  }}