import 'package:flutter/material.dart';import 'package:get/get.dart';class MyController extends GetxController {  var product = 0.obs;  increment() {    product.value++;  }  decrement() {    if (product.value <= 0) {      Get.snackbar(        "Məhsulların alınması:",        "Məhsulun miqdarı sıfırdan az ola bilməz",        icon: Icon(Icons.alarm),        barBlur: 20,        isDismissible: true,        duration: Duration(seconds: 3),      );    } else {      product.value--;    }  }}